import React from 'react';

export default function Card(props) {
  console.log(props.item.openSpots);

  let badgeText;
  if (props.openSpots === 0) {
    badgeText = 'SOLD OUT';
  } else if (props.item.location === 'Online') {
    badgeText = 'ONLINE';
  } else if (props.item.location === 'Norway') {
    badgeText = 'NORWAY';
  } else if (props.item.location === 'Switzerland') {
    badgeText = 'SWITZERLAND';
  }

  return (
    <div className="card">
      {badgeText && <div className="card-badge">{badgeText}</div>}

      <img src={`images/${props.item.coverImg}`} alt="person-img" />

      <div className="card-stats">
        <img src={`props.coverImg`} className="card-img" />
        <span>{props.rating}</span>
        <span className="grey">{props.reviewCount}</span>
        <span className="grey">{props.location}</span>
      </div>
      <p className="card-title">{props.title}</p>
      <p className="card-price">
        <span className="bold">From ${props.price}</span> / person
      </p>
    </div>
  );
}
